css:
.print-format {
    padding: 0;
    margin: 0;
}

@media print {
    .text-overlay1 {
        position: absolute;
        top: 20%; /* Adjusted for cleaner placement */
        left: 50%;
        transform: translateX(-50%); /* Center horizontally */
        color: black;
        font-size: 18px; /* Adjust for readability */
        text-align: center;
        word-wrap: break-word; /* Prevents overflow of long text */
        width: 90%; /* Change to ensure it fits well */
        padding: 5%;
        z-index: 10; /* Ensures text is above the background */
    }
}

@page {
    margin: 0; /* Set page margin to 0 */
}

/* Ensure the back container is positioned relative to control the absolute positioning of child elements */
.back {
    position: relative;
    width: 100%;
    max-width: 100%; /* Prevents overflow in case of large images */
    overflow: hidden;
}

img {
    width: 100%;
    height: auto; /* Maintain the aspect ratio of the image */
    display: block; /* Removes any gaps below the image */
}

/* Styling for the text subject */
.text-subject {
    position: absolute;
    top: 15%; /* Adjusted for a cleaner placement */
    left: 50%;
    transform: translateX(-50%); /* Center horizontally */
    color: black;
    font-size: 30px; /* Adjust for readability */
    text-align: center;
    padding: 0 5%; /* Adds a bit of spacing on left and right */
    word-wrap: break-word; /* Prevents overflow of long text */
    font-weight: bold;
    z-index: 10; /* Ensures text is above the background */
}

/* Ensure the back container is positioned relative to control the absolute positioning of child elements */
.text-overlay1 {
    position: absolute;
    top: 20%; /* Adjusted for cleaner placement */
    left: 50%;
    transform: translateX(-50%); /* Center horizontally */
    color: black;
    font-size: 18px; /* Adjust for readability */
    text-align: center;
    word-wrap: break-word; /* Prevents overflow of long text */
    width: 90%; /* Change to ensure it fits well */
    padding: 7%;
    z-index: 10; /* Ensures text is above the background */
}

/* Make sure the text scales well on smaller screens */
@media (max-width: 768px) {
    .text-subject, .text-overlay1 {
        font-size: 16px;
        padding: 0 3%; /* Adjust the padding for smaller screens */
    }
}
